    words = response.split()
    nouns = [word for word in words if word not in stop_words and word[-1] in ['s', 'd', 'x', 'y', 'z'] or word[-2:] in ['ch','sh','ss', 'll','ss', 'ce', 'ence', 'ance', 'nce','ment','re']]
