```cpp
#include <SoftwareSerial.h>
SoftwareSerial HC05(2, 3); // RX, TX pins for Bluetooth module

// Motor control pins
const int motorLeftForward = 8;
const int motorLeftBackward = 9;
const int motorRightForward = 10;
const int motorRightBackward = 11;

// Bluetooth message handling
const int receivePin = 12; // Pin for receiving Bluetooth messages
const int speedThreshold = 100; // Threshold for speed calculation

int leftSpeed = 0;
int rightSpeed = 0;

void setup() {
  // Initialize serial communication with Arduino IDE
  Serial.begin(9600);

  // Initialize Bluetooth module
  HC05.begin(9600);

  // Initialize motor pins as output
  pinMode(motorLeftForward, OUTPUT);
  pinMode(motorLeftBackward, OUTPUT);
  pinMode(motorRightForward, OUTPUT);
  pinMode(motorRightBackward, OUTPUT);

  pinMode(receivePin, INPUT);
}

void loop() {
  // Read Bluetooth message
  if (HC05.available() > 0) {
    char inChar = HC05.read();
    if (inChar == 'F') {
      // Forward
      leftSpeed = 200;
      rightSpeed = 200;
      digitalWrite(motorLeftForward, HIGH);
      digitalWrite(motorLeftBackward, LOW);
      digitalWrite(motorRightForward, HIGH);
      digitalWrite(motorRightBackward, LOW);
    } else if (inChar == 'B') {
      // Backward
      leftSpeed = -200;
      rightSpeed = -200;
      digitalWrite(motorLeftForward, LOW);
      digitalWrite(motorLeftBackward, HIGH);
      digitalWrite(motorRightForward, LOW);
      digitalWrite(motorRightBackward, HIGH);
    } else if (inChar == 'L') {
      // Left
      leftSpeed = 200;
      rightSpeed = -200;
      digitalWrite(motorLeftForward, HIGH);
      digitalWrite(motorLeftBackward, LOW);
      digitalWrite(motorRightForward, LOW);
      digitalWrite(motorRightBackward, HIGH);
    } else if (inChar == 'R') {
      // Right
      leftSpeed = -200;
      rightSpeed = 200;
      digitalWrite(motorLeftForward, LOW);
      digitalWrite(motorLeftBackward, HIGH);
      digitalWrite(motorRightForward, HIGH);
      digitalWrite(motorRightBackward, LOW);
    } else if (inChar == 'S') {
      // Stop
      leftSpeed = 0;
      rightSpeed = 0;
      digitalWrite(motorLeftForward, LOW);
      digitalWrite(motorLeftBackward, LOW);
      digitalWrite(motorRightForward, LOW);
      digitalWrite(motorRightBackward, LOW);
    }
  }

  // Limit speed to 100 to prevent motor damage
  if (abs(leftSpeed) > speedThreshold) {
    leftSpeed = speedThreshold;
  }
  if (abs(rightSpeed) > speedThreshold) {
    rightSpeed = speedThreshold;
  }

  // Send motor speeds to motor driver
  analogWrite(motorLeftForward, leftSpeed);
  analogWrite(motorRightForward, rightSpeed);
}

```

