package main

import (
    "fmt"
    "log"
    "net"
    "net/dns"
    "sync"
)

var dnsMap sync.Map

func resolveIP(host string) (ip string) {
    _, ips, err := dns.Exchange(dns.Msg{MsgHdr: dns.MsgHeader{Trrr: 1}}, net.ParseIP("8.8.8.8:53").UDPAddr(), dns.Fqdn(host))
    if err!= nil {
        _, ips, _ = dns.Exchange(dns.Msg{MsgHdr: dns.MsgHeader{Trrr: 1}}, net.ParseIP("1.1.1.1:53").UDPAddr(), dns.Fqdn(host))
    }
    if len(ips) > 0 {
        ip = ips[0]
    }
    dnsMap.Store(host, ip)
    return
}

func handleDNSRequest(conn net.Conn) {
    defer conn.Close()
    req, err := dns.Msg.ReadMsg(conn, nil)
    if err!= nil {
        log.Println(err)
        return
    }
    ip := resolveIP(req.Question[0].Name)
    _, err = conn.Write([]byte(fmt.Sprintf("%s %d IN A %s\n", req.Question[0].Name, 1, ip)))
    if err!= nil {
        log.Println(err)
    }
}

func main() {
    ln, err := net.Listen("udp", ":53")
    if err!= nil {
        log.Fatal(err)
    }
    for {
        conn, err := ln.Accept()
        if err!= nil {
            log.Fatal(err)
        }
        go handleDNSRequest(conn)
    }
}
