    "net"
    "net/dns"
    "net/textproto"
    "sync"
    "time"
)

var resolvers = map[string][]string{}

func main() {
    dialer := &net.Dialer{}
    resolver := dns.NewResolver(dialer)

    // Forward requests to 8.8.8.8 and 1.1.1.1
    resolver.Preferred = append(resolver.Preferred, "8.8.8.8:53", "1.1.1.1:53")

    listener, err := net.Listen("tcp", ":53")
    if err!= nil {
        panic(err)
    }
    defer listener.Close()

    var wg sync.WaitGroup
    wg.Add(1)
    go func() {
        defer wg.Done()
        for {
            conn, err := listener.Accept()
            if err!= nil {
                break
            }
            go handleConn(conn)
        }
    }()

    wg.Wait()
}

func handleConn(conn net.Conn) {
    // Parse query
    reader := textproto.NewReader(conn)
    query := dns.Msg{}
    if _, err := reader.ReadLine(); err!= nil {
        conn.Close()
        return
    }

    // Handle query
    if err := query.UnmarshalMsg([]byte(reader.ReadLine())); err!= nil {
        conn.Close()
        return
    }
    defer conn.Close()

    // Forward query to 8.8.8.8 and 1.1.1.1
    var ips []string
    ips = append(ips, "8.8.8.8:53")
    ips = append(ips, "1.1.1.1:53")

    for _, addr := range ips {
        conn2, err := net.Dial("tcp", addr)
        if err!= nil {
            continue
        }
        defer conn2.Close()

        writer := textproto.NewWriter(conn2)
        writer.Init(conn2)

        if err := writer.Writef("%s", query.String()); err!= nil {
            return
        }
        writer.Flush()
        resp, err := dns.ReadMsg(conn2)
        if err!= nil {
            return
        }
        writer.Close()

