Here is a simple DNS server in Go that forwards all requests to 8.8.8.8 and 1.1.1.1 and stores resolved hostnames in a map.

```go
package main

import (
	"bufio"
	"fmt"
	"net"
	"os"
	"strings"
	"sync"
)

var dnsCache = make(map[string]string)
var mutex sync.RWMutex

func dnsServer() {
	l, err := net.ListenUDP("udp", &net.UDPAddr{Port: 8053})
	if err!= nil {
		fmt.Println(err)
		return
	}
	defer l.Close()

	buf := make([]byte, 1024)
	for {
		n, remote, err := l.ReadFromUDP(buf)
		if err!= nil {
			fmt.Println(err)
			continue
		}
		question := string(buf[:n])
		answers := resolveDNS(question)
		l.WriteToUDP([]byte(answers), remote)
		mutex.Lock()
		dnsCache[question] = answers
		mutex.Unlock()
	}
}

func resolveDNS(question string) string {
	parts := strings.SplitN(question, " ", 2)
	if len(parts)!= 2 {
		return ""
	}
	qtype := strings.ToLower(parts[0])
	qname := parts[1]
	addr, err := net.ResolveIPAddr("ip", qtype+"://"+qname)
	if err!= nil {
		return ""
	}
	return addr.IP.String()
}

func main() {
	go dnsServer()
	for {
		time.Sleep(100 * time.Millisecond)
	}
}

```

