@Constraint(validatedBy = UppercaseFirstThreeValidator.class)
@Schema(description = "Check if the first three letters of each word are in uppercase")
public @interface UppercaseFirstThree {
    String message() default "First three letters of each word must be in uppercase";
    Class<?>[] groups() default {};
    Class<? extends Payload>[] payload() default {};
}
