import javax.validation.ConstraintValidator;
import javax.validation.ConstraintValidatorContext;
import java.util.regex.Pattern;

public class UppercaseFirstThreeValidator implements ConstraintValidator<UppercaseFirstThree, String> {

    @Override
    public void initialize(UppercaseFirstThree constraintAnnotation) {}

    @Override
    public boolean isValid(String value, ConstraintValidatorContext context) {
        String[] words = value.split("\\s+");
        for (String word : words) {
            if (word.length() >= 3) {
                if (!word.substring(0, 3).matches("[A-Z]+")) {
                    return false;
                }
            } else {
                return false;
            }
        }
        return true;
    }
}
```

