import { fileURLToPath } from 'url';
import { dirname } from 'path';
import { promises as fs } from 'fs';

function getPackageJsonPath(directory) {
  const dir = fileURLToPath(directory);
  const packageJsonPath = path.join(dir, 'package.json');
  if (await fs.existsSync(packageJsonPath)) {
    return packageJsonPath;
  } else {
    const parentDir = dirname(dir);
    if (parentDir === dir) {
      return null; // 親ディレクトリが自分自身の場合、指定されたディレクトリ内に `package.json` が見つかりませんでした。
    } else {
      return getPackageJsonPath(parentDir);
    }
  }
}

