function distanceFromPoint(point1, point2) {
  const R = 6371; // radius of the Earth in km
  const dLat = (point2[0] - point1[0]) * Math.PI / 180;
  const dLon = (point2[1] - point1[1]) * Math.PI / 180;
  const lat1 = point1[0] * Math.PI / 180;
