// app.js
const express = require('express');
const sqlite3 = require('sqlite3').verbose();
const app = express();
const bcrypt = require('bcryptjs');

app.use(express.json());
app.use(express.urlencoded({ extended: true }));

const db = new sqlite3.Database('./events.db');

app.post('/register', (req, res) => {
  const { username, password } = req.body;
  const hashedPassword = bcrypt.hashSync(password, 10);
  db.run(`INSERT INTO users (username, password) VALUES (?,?)`, [username, hashedPassword], (err) => {
    if (err) res.status(500).send({ message: 'Error creating user' });
    else res.send({ message: 'User created' });
  });
});

app.post('/login', (req, res) => {
  const { username, password } = req.body;
  db.get('SELECT * FROM users WHERE username=?', username, (err, row) => {
    if (err ||!row) res.status(401).send({ message: 'Invalid username or password' });
    else if (bcrypt.compareSync(password, row.password)) res.send({ message: 'Login successful' });
    else res.status(401).send({ message: 'Invalid username or password' });
  });
});

app.post('/event', (req, res) => {
  const { username, event } = req.body;
  db.run(`INSERT INTO events (username, event) VALUES (?,?)`, [username, event], (err) => {
    if (err) res.status(500).send({ message: 'Error logging event' });
    else res.send({ message: 'Event logged' });
  });
});

app.listen(3000, () => {
  console.log('Server running on port 3000');
});
