import { FaArrowDown, FaArrowUp } from'react-icons/fa';
import { tr, th, td, thead, tbody, tfoot, Button, Heading } from './styles';

interface TableProps {
  columns: any[];
  data: any[];
}

const Table = ({ columns, data }: TableProps) => {
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow, state, preFilteredRows, setFilter, headerGroups: HeaderGroup } =
    useTable(
      {
        columns,
        data,
        initialState: {
          sortBy: [{ id: 'name', desc: false }],
          pageSize: 10,
          page: 0,
          sorted: [{ id: 'name', desc: false }],
        },
      },
      useSortBy,
      usePagination,
      useRowSelect,
    );

  const handleSort = (columnId: string) => {
    const currentSort = state.sorted[0].id;
    if (currentSort === columnId) {
      setFilter((prevFilters) => ({...prevFilters, [columnId]: { id: columnId, asc:!state.sorted[0].desc } }));
    } else {
      setFilter({ [columnId]: { id: columnId, asc: true } });
    }
  };

  return (
    <div className="bg-gray-200 h-screen">
      <Heading>Table Example</Heading>
      <table {...getTableProps()} className="table-auto w-full border-collapse border border-gray-400">
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()} key={headerGroup.id}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())} key={column.id}>
                  {column.render('Header')}
                  {column.isSorted? (
                    column.isSortedDesc? (
                      <FaArrowUp className="ml-2 text-blue-500" />
                    ) : (
                      <FaArrowDown className="ml-2 text-blue-500" />
                    )
                  ) : (
                    ''
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()} className="bg-white">
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()} key={row.id}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()} key={cell.column.id}>
                    {cell.render('Cell')}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
        <tfoot>
          <tr {...preFilteredRows.getRowProps()} key="tfoot">
            {preFilteredRows.headers.map((column) => (
              <th {...column.getHeaderProps(column.getSortByToggleProps())} key={column.id}>
                {column.render('Header')}
                {column.isSorted? (
                  column.isSortedDesc? (
                    <FaArrowUp className="ml-2 text-blue-500" />
                  ) : (
                    <FaArrowDown className="ml-2 text-blue-500" />
                  )
                ) : (
                  ''
                )}
              </th>
            ))}
          </tr>
        </tfoot>
      </table>
      <Button
        className="ml-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={() => setFilter({ page: state.page + 1 })}
      >
        Next
      </Button>
      <Button
        className="ml-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={() => setFilter({ page: state.page - 1 })}
      >
        Prev
      </Button>
      <p className="ml-4 text-gray-500">Page {state.page + 1} of {Math.ceil(data.length / state.pageSize)}</p>
    </div>
  );
};

export default Table;
```

