// components/Table.tsx
import React from'react';
import { useTable, useGlobalFilter, useSortBy } from'react-table';
import NoRows from './NoRows';
import styles from './Table.module.css';

interface TableProps {
  columns: any[];
  data: any[];
  isLoading: boolean;
  isNoData: boolean;
  rowStyle?: string;
  tableVariant?: 'dark' | 'light';
  headerStyle?: string;
  hoverStyle?: string;
}

const Table: React.FC<TableProps> = ({
  columns,
  data,
  isLoading,
  isNoData,
  rowStyle ='stripe',
  tableVariant = 'light',
  headerStyle ='sticky',
  hoverStyle = 'bg-blue-100',
}) => {
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow, state, preGlobalFilteredRows, setGlobalFilter } =
    useTable(
      {
        columns,
        data,
        autoResetHiddenColumns: true,
        initialState: { hiddenColumns: [] },
      },
      useGlobalFilter,
      useSortBy
    );

  return (
    <div className="overflow-x-auto">
      <table
        {...getTableProps()}
        className={`table-auto w-full ${styles.table} ${
          tableVariant === 'dark'
           ? 'border-collapse border-b border-gray-600'
            : 'border-collapse border-b border-gray-200'
        }`}
      >
        <thead className={`sticky top-0 bg-${tableVariant === 'dark'? 'gray-700' : 'white'} z-10`}>
          {headerGroups.map((headerGroup) => (
            <tr
              {...headerGroup.getHeaderGroupProps()}
              className={`uppercase ${styles[headerStyle]} text-sm font-medium text-${tableVariant === 'dark'? 'gray-300' : 'gray-700'}`}
            >
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render('Header')}
                  <span className="inline-block ml-1 text-xs font-medium text-${tableVariant === 'dark'? 'gray-300' : 'gray-700'}">
                    <svg
                      className="w-4 h-4 inline-block"
                      fill="currentColor"
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                      strokeWidth="1.5"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    >
                      <path d="M12 9v2m0 4h.01M6 3L5 3 5 5l1.01 1.01M14 14V2" />
                    </svg>
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()} className="bg-white dark:bg-gray-800">
          {isLoading? (
            <tr>
              <td colSpan={columns.length} className="text-gray-500">
                <span className="animate-pulse h-5 w-48 rounded-lg bg-gray-400 dark:bg-gray-600"></span>
                <span className="animate-pulse h-5 w-48 rounded-lg bg-gray-400 dark:bg-gray-600"></span>
              </td>
            </tr>
          ) : isNoData? (
            <tr>
              <td colSpan={columns.length}>
                <NoRows message="No data available" />
              </td>
            </tr>
          ) : (
            rows.map((row) => {
              prepareRow(row);
              return (
                <tr
                  {...row.getRowProps()}
                  key={row.id}
                  className={`odd:bg-${row.isEven? 'white' : 'white'} ${styles[rowStyle]} hover:bg-${hoverStyle}`}
                >
                  {row.cells.map((cell, index) => (
                    <td {...cell.getCellProps()} key={index}>
                      {cell.render('Cell')}
                    </td>
                  ))}
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
