// Create a new Redis client
const redis = new Redis({
  host: 'localhost',
  port: 6379,
  db: 0,
});

// Define the Redis search function
async function createIndex() {
  // Create a new index
  await redis.send_command('FT.CREATE', {
    INDEX: 'idx',
    PREFIX:'myindex:',
    SCHEMA: [
      { FIELD: 'title', TYPE: 'TEXT' },
      { FIELD: 'description', TYPE: 'TEXT' },
    ],
  });
