  await redis.send_command('FT.ADD', {
    INDEX: 'idx',
    ID: 'doc:1',
    CONTENTS: JSON.stringify({ title, description }),
    SCHEMA: ['myindex:title','myindex:description'],
  });
}

// Define the function to search for a query
async function search(query) {
  return await redis.send_command('FT.SEARCH', {
    INDEX: 'idx',
    QUERY: {
      MATCH: `*${query}*`,
      Fields: ['title', 'description'],
    },
    LIMIT: 0,
    RETURN: ['title', 'description'],
  });
}

