void perm_next(PERM *perm) {
    int i;
    for (i = perm->size - 2; i >= 0; i--) {
        if (perm->elements[i] < perm->elements[i + 1]) {
            break;
        }
    }
    if (i == -1) {
        // This is the last permutation, so there's no next one
        return;
    }
    int j;
    for (j = perm->size - 1; j > i; j--) {
        if (perm->elements[i] < perm->elements[j]) {
            break;
        }
    }
    int temp = perm->elements[i];
    perm->elements[i] = perm->elements[j];
    perm->elements[j] = temp;
    // Sort the right side of the swapped elements in ascending order
    for (int k = j + 1; k < perm->size; k++) {
        for (int l = k; l > j + 1; l--) {
            if (perm->elements[l - 1] < perm->elements[l]) {
                int temp2 = perm->elements[l - 1];
                perm->elements[l - 1] = perm->elements[l];
                perm->elements[l] = temp2;
            } else {
                break;
            }
        }
    }
}
