// Structure to represent a multiset
typedef struct {
    int* elements;
    int size;
} Multiset;

// Function to initialize a multiset
Multiset* multiset_init(int n, int* elements) {
    Multiset* ms = (Multiset*) malloc(sizeof(Multiset));
    ms->elements = (int*) malloc(n * sizeof(int));
    ms->size = n;
    for (int i = 0; i < n; i++) {
        ms->elements[i] = elements[i];
    }
    return ms;
}

// Function to free a multiset
void multiset_free(Multiset* ms) {
    free(ms->elements);
    free(ms);
}

// Function to calculate the frequency of a pair in the multiset
int get_pair_frequency(Multiset* ms, int i, int j) {
    int freq = 0;
    for (int k = 0; k < ms->size; k++) {
        if (ms->elements[k] == ms->elements[i] && k < j) freq++;
        if (ms->elements[k] == ms->elements[j] && k > i) freq++;
    }
    return freq;
}

// Function to calculate the permutation score
int perm_score(Multiset* ms, int* permutation) {
    int score = 0;
    for (int i = 0; i < ms->size - 1; i++) {
        for (int j = i + 1; j < ms->size; j++) {
            if (ms->elements[j] < ms->elements[i] && get_pair_frequency(ms, i, j) > 0) score++;
        }
    }
    return score;
}

// Function to generate the next permutation
void next_permutation(int* permutation, int n) {
    int i = n - 2;
    while (i >= 0 && permutation[i] >= permutation[i + 1]) i--;
    if (i < 0) {
        // The permutations are exhausted
        return;
    }
    int j = n - 1;
    while (permutation[j] <= permutation[i]) j--;
    int temp = permutation[i];
    permutation[i] = permutation[j];
    permutation[j] = temp;
    int k = i + 1;
    while (k < n && permutation[k] < permutation[i]) k++;
    int* end = permutation + i + 1;
    int* end_temp = permutation + k;
    for (int* ptr1 = end, * ptr2 = end_temp; ptr2 < permutation + n; ptr1++, ptr2++) {
        *ptr1 = *ptr2;
    }
}

// Function to implement the PERM algorithm
int perm_algorithm(Multiset* ms) {
    int permutation[ms->size];
    for (int i = 0; i < ms->size; i++) {
        permutation[i] = i;
    }
    int best_perm = 0;
    int current_perm = perm_score(ms, permutation);
    while (1) {
        next_permutation(permutation, ms->size);
        int new_perm = perm_score(ms, permutation);
        if (new_perm > current_perm) {
            current_perm = new_perm;
            if (new_perm > best_perm) best_perm = new_perm;
        } else {
            // The permutations are exhausted
            return best_perm;
        }
    }
}

